mapper.delete:
DELETE
FROM POST
WHERE
POST_ID = #{postId}
AND BOARD_ID = #{boardId}

mapper.update:
UPDATE POST
SET HIT = HIT + 1
WHERE POST_ID = #{postId}
AND BOARD_ID = #{boardId}

UPDATE POST SET
TITLE =
#{title}
, CONTENT = #{content}
, MODIFY_DATE = sysdate
WHERE POST_ID =
#{postId}
AND BOARD_ID =
#{boardId}
